int jwsPagStaticTable_load(netsnmp_cache *cache, void *magic)
{
    int ret;
    int i, channel_num;
    struct jwsPagStaticTable_entry table_entry;

    ret = ubus_invoke_command("port_aggr", "port_aggr_get", get_ret_handler_cb, 
                                "{\"get_args\":[\"channel_num\"]}");
    if (ret != 0)
    {
        LogWrite(ERROR, "file:%s line:%d func:%s, invoke get_args channel_num failed\n",
                        __FILENAME__, __LINE__, __FUNCTION__);
        return -1;
    }

    channel_num = atoi(jws_read_buf_str());
    if (-1 == channel_num)
    {
        LogWrite(ERROR, "file:%s line:%d func:%s, static jwsPagMode disable\n",
                        __FILENAME__, __LINE__, __FUNCTION__);
        return -1;
    }

    jwsPagStaticTable_removeTable();
    for (i = 1; i <= channel_num; i++)
    {
        table_entry.jwsPagChannelRowStatus = 1;
        jwsPagStaticTable_createEntry(i, &table_entry);
    }

    return 0;
}

