int jwsStaticMcastTable_load(netsnmp_cache *cache, void *magic)
{
    int ret;
    int i, group_num;
    struct jwsStaticMcastTable_entry table_entry;

    ret = ubus_invoke_command("mcast_config", "mcast_config_get", get_ret_handler_cb, 
                                "{\"get_args\":[\"group_num\"]}");
    if (ret != 0) {
        LogWrite(ERROR, "file:%s line:%d func:%s, invoke get_args group_num failed\n",
                        __FILENAME__, __LINE__, __FUNCTION__);
        return -1;
    }

    group_num = atoi(jws_read_buf_str());
    if (group_num == -1)
    {
        LogWrite(ERROR, "file:%s line:%d func:%s, static multicast disable\n",
                        __FILENAME__, __LINE__, __FUNCTION__);
        return -1;
    }

    jwsStaticMcastTable_removeTable();
         
    for (i = 1; i <= group_num; i++) {
        table_entry.jwsStaticMcastRowStatus = 1;
        jwsStaticMcastTable_createEntry(i, &table_entry);
    }

    return 0;
}

